catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

    var schDa = g_form.getDisplayValue('scheduled_date');
    var ga = new GlideAjax('x_568580_class_a_0.classRepetationAvoid');
    ga.addParam('sysparm_name', 'checkAvail');
    ga.addParam('sysparm_schDate', schDa);
    ga.addParam('sysparm_ses', newValue);
    ga.getXML(HelloWorldParse);

    function HelloWorldParse(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");

        if (answer) {
            g_form.addErrorMessage("There is another class scheduled for the same time. Please consider choosing an alternative time slot & kindly check schedules");
            g_form.clearValue('scheduled_date');
        }

    }


}
