sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
var v = new sn_pdfgeneratorutils.PDFGenerationAPI;
var temp=current.template.temphtml;
var tableName='x_568580_class_a_0_report_capture';
var tableSysId=current.sys_id;
var fileName= current.subject+"weeklyRPT";
var schCount=0;
var tp=0;
var ta=0;
var sIn="";
var gd = new GlideDate();


var sch=new GlideRecord('x_568580_class_a_0_scheduled_classes');
sch.addQuery('subject',current.subject);
sch.query();
sch.orderBy('scheduled_date');
while(sch.next()){
	sIn=sch.getDisplayValue('subject_teacher');
	schCount++;
}
var attUtil=new x_568580_class_a_0.AttendanceReporter();
var inhtl=attUtil.attAbsent(current.subject,'uninformed_leave');

tp=parseInt(attUtil.attCounter(current.subject,'present'));
ta=parseInt(attUtil.attCounter(current.subject,'absent'));



var html=temp.replace('TBH',current.subject+'-Weekly Report');
html=html.replace('date',current.getDisplayValue('sys_created_on'));
html=html.replace('subj',current.subject);
html=html.replace('subjTeacher',sIn);
html=html.replace('bt','MCASet 31');
html=html.replace('prd',current.from +"-"+gd.getValue());
html=html.replace('Tbd',inhtl);
html=html.replace('cno',schCount);
html=html.replace('pno',tp);
html=html.replace('ano',ta);

var result = v.convertToPDF(html,tableName,tableSysId,fileName);


current.setValue('state','Sent');
current.update();
gs.info(JSON.stringify(result));


})(current, previous);
