sys_script_include.script:
var ScheduleValidate = Class.create();
ScheduleValidate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    checkDate: function(dateScheduled) {
		try{
        var count;
        var mess;
        var schClass = new GlideAggregate('x_568580_class_a_0_scheduled_classes');
        schClass.addAggregate('COUNT');
        schClass.groupBy('scheduled_date');
        schClass.addQuery('scheduled_date', dateScheduled);
        schClass.query();
        count = schClass.getAggregate('COUNT');
        if (count <= 4) {
            mess = "ok";
        } else {
            mess = "no";
        }

        return mess;
		}catch(e){
			gs.log("preventsch:"+e);
		}


    },

    type: 'ScheduleValidate'
});
